void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 2.0;

    vec2 uv = scale*fragCoord.xy / iResolution.xx;

    vec2 uv2 = fract(8.0*uv + 0.5) - 0.5; // add and subtract half a unit to get centered lines 
    vec2 uv3 = fract(1.0*uv  + 0.5) - 0.5;


    float t = 0.01;

    // thickness thin line
    float d2 = 1.0*scale*t;

    // thickness thick line
    float d3 = 2.0*scale*t/8.0;


    // background
    fragColor = vec4(1.0, 1.0, 1.0, 1.0);

    // thick lines every 8x8
    if (abs(uv3.x) < d3 || abs(uv3.y) < d3)
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    // thin lin every 1x1
    if (abs(uv2.x) < d2 || abs(uv2.y) < d2)
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);


}
