float sdsphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdtorus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q) - t.y;
}

float displacement(vec3 p, float c)
{
	return sin(c * p.x) * sin(c * p.y) * sin(c * p.z);
}

float sdspheredisplace(vec3 p, float r, float c)
{
    float d1 = sdsphere(p, r);
    float d2 = displacement(p, c);
    return d1 + d2;
}

float map(vec3 p)
{
    float a = sdspheredisplace(p, 0.002, texture(iChannel0, vec2(0, 0)).x * 8.0);
    float b = sdtorus(p, vec2(0.2, 0.05));

	return min(a, b);
}

float march(vec3 ro, vec3 rd)
{
	float t = 0.0;
    for(int i = 0; i < 64; ++i)
    {
        vec3 p = ro + rd * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(cos(iTime) * 0.1, sin(iTime) * 0.1, -0.5 + sin(iTime) * 0.1);
    vec3 rd = normalize(vec3(uv, 1.0));

    float d = march(ro, rd);
    float r = step(d, 0.99);

    vec4 bg = vec4(0.2, 0.3, 0.4, 1.0);
    vec4 fg = vec4(0.4, 0.5, 0.0, 0.0);

    fg.z += texture(iChannel0, vec2(0.2, 0)).x * 10.0;

    fragColor = bg + fg * r * d;

}
