void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
	vec3 c = vec3(0.,0.,1.);

	float x = .5+floor(fragCoord.x*9. / iResolution.x)/9.;
	float y = .5+floor(fragCoord.y*5. / iResolution.y)/5.;
	c = vec3(mix(c, vec3(x*y)*.5, 1.-clamp(length(mod(uv, .2)/.2 - .5)*100.-45., 0., 1.)));
	fragColor.rgb = c;

	if(mod(uv.x, .2) > .1 || mod(uv.y, .2) < .1) {
		float t = fract(sin(uv.x*12.9898+uv.y*78.233)*43758.5453123);
		fragColor.rgb = step(1.-pow(fragColor.rgb, vec3(2.2)), vec3(t));
	}

}
