float xsample(vec2 uv, float t) {
	uv*=0.03;
	float r=length(uv);	
t*=(10.+floor(r))*0.1;	
	t*=0.0015;
	t=t*t*t*t*t;
	t*=sign(fract(r)-0.5);
	float c=cos(t),s=sin(t);
	uv=mat2(c,s,-s,c)*uv;
	return step(0.5,fract(uv.x));
}
// s/rand based on justaway's https://www.shadertoy.com/view/MdXXWr
float srand(vec2 a) { return sin(dot(a,vec2(1233.224,1743.335))); }
vec2 rand(vec2 r) { return fract(3712.65*r+0.61432); }	
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy-iResolution.xy*0.5;	
	vec2 aa=vec2(srand(uv.xy),srand(uv.yx));
	float c=0.,t=iTime * 60.0 + aa.x/64.0;
	for (int i=0;i<64;++i) {
		aa=rand(aa);
		c+=xsample(uv+aa,t+aa.y/64.);
		t+=1./64.;
	}
	c=sqrt(c/64.);
	fragColor = vec4(c,c,c,1.0);
}
