//https://en.wikipedia.org/wiki/Alpha_compositing

//optionally skip the main thing, showing a simpler thing without Alpha_compositing
#define Alpha_compositing

//zoom
#define camLens 1.
//set frame setup
#define frame(u) camLens*(u-.5*iResolution.xy)/iResolution.y

//circle properties
#define radius .3
#define blur .5

//texture properties
#define c0(u)   texture(iChannel0,u).xyz
#define c1(u)   texture(iChannel1,u).xyz
//#define cyan    vec3(0,1,1)
//#define magenta vec3(1,0,1)
//#define yellow  vec3(1,1,0)

//Golden ratio, the 2 solutions for; 1./x=x-1.; reciprocal==Derivative <-> least shade.
#define phi (sqrt(5.)*.5-.5)
#define Phi (sqrt(5.)*.5+.5)

//https://en.wikipedia.org/wiki/Alpha_compositing
vec4 aOverB(vec4 a,vec4 b){a.xyz*=a.w;b.xyz*=b.w;return vec4(a+b*(1.-a));}
//not sure if correct, but looks useful.
vec4 aXorB(vec4 a,vec4 b){a.xyz*=a.w;b.xyz*=b.w;return vec4(a*(1.-b)+b*(1.-a));}

#define ss(a) smoothstep(blur,-blur,a);
void mainImage(out vec4 O,in vec2 U){
 vec2 u=frame(U); 
 vec2 m=frame(iMouse.xy);
 vec2 n=frame(iMouse.zw);
 if(iMouse.z<=0.){
  m=.5*vec2(cos(iTime*Phi),0.);
  n=.5*vec2(sin(iTime),cos(iTime));
 }
 float a=length(m-u);a=ss(a-radius);
 float b=length(n-u);b=ss(b-radius);
 #ifndef Alpha_compositing
  O=vec4(a,0,b,1);//2 color channels are set by mouse positions.
 #else 
  vec4 a4=vec4(c0(u),a);//colors are set by 
  vec4 b4=vec4(c1(u),b);//alpha channels are set by distance to mouse positions.
  O=aXorB(a4,b4);  
 #endif
}
