void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.x;
	float col = 0.0;
    uv.x -= 0.5;
    uv.y -= iResolution.y/iResolution.x/2.0;
    float dir = atan(uv.x, uv.y)*10.0+iTime*10.0;
    if(sin(dir-pow(length(uv),3.0)*500.0) > 0.0) {
    	col += 0.25;
    }
    dir = atan(uv.x, uv.y)*10.0+iTime*11.0;
    if(sin(dir-pow(length(uv),3.0)*300.0) > 0.0) {
    	col += 0.25;
    }
    dir = atan(uv.x, uv.y)*10.0+iTime*9.0;
    if(sin(dir-pow(length(uv),3.0)*400.0) > 0.0) {
    	col += 0.25;
    }
    dir = atan(uv.x, uv.y)*10.0+iTime*12.0;
    if(sin(dir-pow(length(uv),3.0)*600.0) > 0.0) {
    	col += 0.25;
    }
	fragColor = vec4(vec3(col),1.0);
}
