void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;

    float pi = 3.14159265359;
    float bpm = 1.0 / 60.0 * 94.0;
    float t = iTime;
    float bt = t * bpm;

    float ts = 1.0 / 32.0;
    float t0 = texture(iChannel0, vec2(ts * 0.0, 0.25)).x;
    float t1 = texture(iChannel0, vec2(ts * 1.0, 0.25)).x;
    float t2 = texture(iChannel0, vec2(ts * 2.0, 0.25)).x;
    float tuv = texture(iChannel0, vec2(uv.y, 0.25)).x;

    vec3 c = vec3(0.0);

    c.yz += sin(bt * pi) * 0.3 * (0.0 - abs(nuv.x * sin(2000.0) * sin(bt * 2.0)));
    c.y *= pow(tuv, 3.0 * uv.y);
    c.z = pow(t1 * 0.1 + 0.2, 0.2 + uv.y * 4.0);

    c.z += sin(uv.y * 320.0) * c.y;

    float fg = smoothstep(0.0, 1.0, step(0.9 - t1 * 0.2, sin(uv.x * 320.0) * sin(uv.y * 180.0) * pow(t1, 2.0)) * 0.5 * t2 * (1.0 - abs(nuv.x)));

    fg *= length(nuv.y) * 0.1 + 0.1 * tuv;

    float tf = texture(iChannel1, vec2(nuv.x * tanh(nuv.y + sin(bt * pi)))).x;

    c += fg * tf * 12.0 * pow(t1, 1.5) * sin(nuv.y * 20.0 + t0 * 12.0);

    vec3 col = vec3(
        1.3 * c.x,
        1.4 * c.y + c.x,
        0.9 * c.z
    );

    col += vec3(0.1, 0.2, 0.2);

    fragColor.xyz = col;
}
