void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 video = texture(iChannel0,uv);

    float viewRange = 75.0;
    float blurDistance = 20.0;
    float diff = viewRange - blurDistance;

	float dist = distance(vec2(iMouse.x, iMouse.y), fragCoord);

    vec4 spotLight = vec4(0.0, 0.0, 0.0, 1.0);

    if(dist < viewRange)
    {
        if(dist > diff)
        {
            float diff2 = viewRange - dist;
            spotLight = video * (diff2/blurDistance);
        }
        else
        {
            spotLight = video;
        }
    }

    fragColor = mix(spotLight, video, 0.0);
}
