void mainImage( out vec4 O, vec2 U )
{
	O = texture(iChannel0, U/iResolution.xy);
  //O = smoothstep(.3,.7,O);
}

void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy;
  //  if (iFrame<10) { O = texture(iChannel1,U); return; }

#define T(l) ( ( textureLod(iChannel1,U+.0*iTime,l) -.5 )*exp2(l*2.4) +.5 )

    vec4 H = ( T(0.)+T(1.)+T(2.)+T(3.)+T(4.)+T(5.)+T(6.) ) / 7.;
    float l = length(H.rgb);
  //O = .5+.5*sin(l/vec4(1,5,10,0)); return;
    O = mix( texture(iChannel1,U), //.xxxx, 
             texture(iChannel0, fract( U - vec2(dFdx(l),dFdy(l)) / 360. )),
             .96);
}
