float C(vec2 v)
{
    v.y += iTime;
    //float t = iTime;
    //v += vec2(cos(t),sin(t));
    v = fract(v)-.5;
    return step(0.,v.x*v.y);
}

void mainImage( out vec4 O, in vec2 I )
{
	vec2 R = iResolution.xy;
    I = (I+I-R)/R.y;
    //float d = step(I.y,.0);
    float ang = atan(I.y,I.x);
    I /= vec2(2., I.y) * abs(I.y);
        I.y *= cos(ang+iTime);

    O -= O;
    O += C(I*2.)/I.y/I.y;
    //O += 1.- d*C(I*2.)/I.y/I.y;
}
