void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tex00 = texture(iChannel0, uv);

    float gt = 0.0;//iTime*.2;
    vec3 px = vec3(0.99/iChannelResolution[1].xy,0.0);
    vec4 tex10 = texture(iChannel1, uv - px.xz);
    vec4 tex20 = texture(iChannel1, uv + px.xz);
    vec4 tex01 = texture(iChannel1, uv - px.zy);
    vec4 tex02 = texture(iChannel1, uv + px.zy);
    vec4 tex11 = texture(iChannel2, (-1.0+0.25*fragCoord.xy / iChannelResolution[2].xy)
                           *mat2(sin(gt),cos(gt),
                                 -cos(gt),sin(gt)));// - px.xy);
    //vec4 tex22 = texture(iChannel2, uv + px.xy);
    //tex11 = clamp(tex11*4.0,0.0,1.0);

    float minDist = 2.0;
    float dist;
    vec4 minTex = tex00;
    //tex00 *= tex00.bgra;
    if ((dist = distance(tex00,tex10))<minDist) { minDist = dist; minTex = tex10; }
    if ((dist = distance(tex00,tex01))<minDist) { minDist = dist; minTex = tex01; }
    if ((dist = distance(tex00,tex20))<minDist) { minDist = dist; minTex = tex20; }
    if ((dist = distance(tex00,tex02))<minDist) { minDist = dist; minTex = tex02; }

    tex00 += smoothstep(min(min(tex00.r,tex00.g),tex00.b),max(max(tex00.r,tex00.g),tex00.b),tex00);
    tex00 /= 2.0;
    if ((dist = distance(tex00,tex11))<minDist) { 
        minDist = dist; 
        minTex = tex11;///max(max(tex11.r,tex11.g),tex11.b);
    }
    //if ((dist = distance(tex00,tex22))<minDist) { minDist = dist; minTex = tex22; }

    fragColor = minTex;
}
