// blend color * mask ( antialiased ellipse )
#define B(c,a) o = mix( o, c, smoothstep( 1.5, 0., (length(a)-1.) / fwidth(length(a))))

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, V;
    U = ( (U+U-R)/R.y + vec2(0,1) ) / vec2(.4,.2);
    float o = 0., a;

    for (float y = -18.-fract(iTime); y < 19.; y++)
        V = U,
        V.y +=  -.3*( y+19. ),
        a = y<=-2. ? 0. : y >= 2. ? 3.14 : 3.14*(y+2.)/4., // angle
        V.x -= .5*cos(a)/.4 , V -= .2*sin(a)/.2, // horizontal rotation
        B(.4+.4*V.x, V+vec2(0,.27)),             // blend bottom
        B(.5+.1*V.y, V   ),                      // blend top disk
        B(0.,        V/.2);                      // blend hole

    O = o* vec4(1,.9,.8,0);
}
