float plot(vec2 st, float pct){
  return  smoothstep( pct-0.2, pct, st.y) -
          smoothstep( pct, pct+0.2, st.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.);

	color += vec3(plot(vec2(uv.x), 0.), 0., 0.);
    color += vec3(plot(vec2(uv.x), .2), plot(vec2(uv.x), .2), 0.);
    color += vec3(0., plot(vec2(uv.x), .4), 0.);
    color += vec3(0., plot(vec2(uv.x), .5), plot(vec2(uv.x), .5));
    color += vec3(0., 0., plot(vec2(uv.x), .7));
    color += vec3(plot(vec2(uv.x), .8), 0., plot(vec2(uv.x), .8));
    color += vec3(plot(vec2(uv.x), 1.), 0., 0.);

    // Output to screen
    fragColor = vec4(color,1.0);
}
