//Distance functions

float box( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

//Smoothing function by iq.
//Great source for computer graphics: https://iquilezles.org/
vec2 smin(vec2 ca, vec2 cb, float k)
{
    float a = ca.x;
    float b = cb.x;
 	float h = clamp(0.5 + 0.5*(b-a)/k, 0., 1.);
    return mix(cb,ca,h)-k*h*(1.-h);
}
float length8(vec2 p)
{
 return pow(pow(p.x,8.)+pow(p.y,8.),1./8.);
}
float sdTorus82( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
vec3 opRep( vec3 p, vec3 c )
{
    vec3 q = mod(p,c)-0.5*c;
    return q;
}
float sdTorus88( vec3 p, vec2 t )
{
  vec2 q = vec2(length8(p.xz)-t.x,p.y);
  return length8(q)-t.y;
}
vec2 opI( vec2 d1, vec2 d2 )
{
    return vec2(max(d1.x,d2.x));
}
vec2 opS( vec2 d1, vec2 d2 )
{
    return vec2(max(-d1,d2));
}
float sphere( vec3 p, float s )
