vec3 calcLight(vec3 p, vec2 object, vec3 rd,vec3 cam)
{
    vec2 eps = vec2(0.0, EPSILON);
    vec2 d1 = distFunc(p + eps.yxx,cam);
    vec2 d2 = distFunc(p - eps.yxx,cam);
	vec3 normal = normalize(vec3(
    d1.x - d2.x,
    distFunc(p + eps.xyx,cam).x - distFunc(p - eps.xyx,cam).x,
    distFunc(p + eps.xxy,cam).x - distFunc(p - eps.xxy,cam).x));

    vec3 diffuse = vec3(max(0.0, dot(-rd, normal)));

    diffuse *= texture(iChannel0, vec2(p.x*p.z,p.z*p.y)*5.).rgb*3.;


