   	diffuse*=color;

	return vec3(diffuse + specular);

}


vec3 setCamera( in vec3 eye, in vec3 target, in vec2 uv, in float angle )
{
    float fov = 3.0;

	vec3 forward = normalize( target - eye );
//    vec3 up = normalize( cross(forward,vec3(sin(angle), cos(angle),0.0) ) );
    vec3 up = normalize( cross(forward,vec3(0.,0.,-1.0) ) );
    vec3 right = normalize( cross(up,forward));
	vec3 raydir = normalize( uv.x*up + uv.y*right + fov*forward );
	return raydir;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv*=2.;
    uv-=1.;
    uv.x*=iResolution.x/iResolution.y;

    float state = mod(iTime/10.,3.);

    vec3 eye = vec3(cos(iTime)*2.1-2.,-2.5, sin(iTime)*0.2);
   // eye=vec3(0.,-1.,-0.1);
    vec3 lookAt =  vec3(cos(iTime)*2.1-2.,0.5, 0.);
   // state =2.31;
    if(state < 1.)
    {
        //default
    }
    else if(state <2.3)
    {
      	eye = vec3(17.*(-state +1.35),-0.7*(state-1.)-0.5,( state-1.)*1.6);
   		lookAt =  vec3(1.2*(-state ),0.5, 0.);
    }
    else if(state > 2.3)
    {
        float d = state-2.3;
      	eye = vec3(-d*16.5+3., 0.3,-0.6);
   		lookAt =  vec3(1.,0.0, 3.);
    }

    vec3 rayDir = setCamera(eye, lookAt, uv, 0.6);
    uv = fragCoord.xy / iResolution.xy;
    uv.x*=iResolution.x/iResolution.y;

    vec3 ro = eye;


    float t = 0.;


    vec3 color = vec3(0.);

    for(int i =0; i<STEPS;i++){
        vec3 p = rayDir*t+ro;
        vec2 dist = distFunc(p,eye);
        if(dist.x < EPSILON)
        {
         	color = calcLight(p,dist,rayDir,eye);   
            break;
        }
        if(t > MAX_DIST)
        {
         	//color = vec3(0., 1., 0.);
