            color*=vec3(sin(iTime)*0.5+0.5, sin(iTime*0.4)*0.5+0.5, sin(iTime*3.)*0.5+0.5);
            break;
        }
        t += dist.x;

    }
	fragColor = vec4(color,1.0);
}
