#define ITERATIONS 200.0
#define M_PI 3.1415926535897932384626433832795

vec3 hsv2rgb( vec3 c )
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 series( vec2 z )
{
    vec2 sum = vec2(0,0);
    vec2 c;
 	for( float i = 1.0; i < ITERATIONS; ++i )
    {
        float t = z.y * log( i );
        c = pow( i, z.x ) * vec2( cos(t), sin(t) );
        float div = c.x*c.x + c.y*c.y;
        sum += vec2( c.x / div, -c.y / div );
    }
    return sum;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float m = sin( iTime / 50.0 ) * 500.0 + 600.0;
    vec2 uv = ( fragCoord - iResolution.xy / 2.0 ) / 500.0 + vec2(1,1);
    vec2 sum = series( uv );
    vec3 color = vec3(
    	atan( sum.y / sum.x ) * m,	
        1,     
        1									
    );
	fragColor = vec4( hsv2rgb( color ), 1 );
}
