#define scale 0.1
#define pi 3.1415

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.0*(mod(fragCoord, iResolution.xy/2.) - .25*iResolution.xy)/iResolution.y;
 	float dUV = 2.*length(vec2(dFdx(uv.x), dFdy(uv.y)));
    vec2 polar = vec2(length(uv), atan(uv.x, uv.y));
    float l = mod(polar.x, scale);
    float ring = smoothstep(0.033,0.033 + dUV,l);
    ring *= 1.-smoothstep(0.053, 0.053 + dUV, l);
    ring = 1.-ring;

    float theta = polar.y+iTime;
    float blue = smoothstep(pi/16.-0.01, pi/16.+ dUV, mod(theta, pi/4.));
    blue *= 1.- smoothstep(3.*pi/16.-0.01, 3.*pi/16. + dUV, mod(theta, pi/4.));

    vec3 b = (1.-ring)*vec3(0.,0.,1.)*blue;

    fragColor = vec4(ring + b, 1.);
}
