void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

// Horizontal blur

#define G(i) T(i, 0)*F(float(i))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 col = vec4(0.0);
    col += BLUR;
    col /= col.w;
    fragColor = col;
}

// Vertical blur

#define G(i) T(0, i)*F(float(i))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 col = vec4(0.0);
    col += BLUR;
    col /= col.w;
    fragColor = col;
}

// I know about texelFetch, but my browser doesn't seem to support it :(
#define T(x,y) vec4(texture(iChannel0, fract((fragCoord+vec2(x,y))/iResolution.xy)).xyz, 1)
#define F(i) (f(i))
#define BLUR (G(-4) + G(-3) + G(-2) + G(-1) + G(0) + G(1) + G(2) + G(3) + G(4))

const float PI = atan(0., -1.);
float f(float i)
{
    return exp(-i*i/4.)/sqrt(4.*PI);
}

// Transformation

mat2 rot(float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy-0.5;
    if (iFrame < 30 || texture(iChannel3, vec2(32./255., 0.0)).x > 0.5)
    {
        fragColor = texture(iChannel1, uv+0.5);
        return;
    }
    uv = uv*rot(0.001)*0.99;
    vec4 col = texture(iChannel0, fract(uv+0.5));
    fragColor = col;
}

// Sharpening

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy-0.5;
    vec4 col = T(-1, -1)*0.05 + T( 0, -1)*0.20 + T( 1, -1)*0.05 +
               T(-1,  0)*0.20 - T( 0,  0)*1.00 + T( 1,  0)*0.20 +
               T(-1,  1)*0.05 + T( 0,  1)*0.20 + T( 1,  1)*0.05;
    col /= 8.0;
    col = T(0, 0) - col*50.0;
    col -= 0.5;
    col = col*0.7;
    col = smoothstep(vec4(-0.5), vec4(0.5), col);
    col = mix(col, vec4(length(col.xyz)/sqrt(3.)), 0.01);
    fragColor = col;
}
