float bit(float x, int bit) {
    x = fract(x * exp2(float(-bit - 1)));
    return x < 0.5 ? 0.0 : 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float xbit = bit(fragCoord.x, 0);
    float ybit = bit(fragCoord.y, 0);
    float tbit = bit(float(iFrame), 0);
    float sbit = bit(float(iFrame), 5);

    float y = fragCoord.x / iResolution.x;

    float c = 0.0;

    if (y < 0.33) {
    	c = bit(xbit + tbit + sbit, 0);
    } else if (y < 0.66) {
    	c = bit(ybit + tbit + sbit, 0);
    } else {
    	c = bit(xbit + ybit + tbit + sbit, 0);
    }

    float a = 0.2;
    float b = 0.7;

    vec4 c1 = vec4(a, a, a, 1.0);
    vec4 c2 = vec4(b, b, b ,1.0);

    fragColor = c == 0.0 ? c1 : c2;
}
