vec2 calc_point(float radius, float angle, float center_x, float center_y)
{
    vec2 point;
    point.x = center_x + radius*cos(angle);
    point.y = center_y + radius*sin(angle);

    return point;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(1.0, 1.0, 1.0); 

    float r = sin(iTime)*100.0;
    float thickness = 2.0;

    bool finded = false;
    for (float i=0.0; i!=360.0; ++i)
    {
        vec2 point = calc_point(r, i, iMouse.x, iMouse.y);
        if (gl_FragCoord.x - thickness < point.x && gl_FragCoord.x + thickness > point.x
           && gl_FragCoord.y - thickness < point.y && gl_FragCoord.y + thickness > point.y)
        {
            color.x = max(sin(iTime), 0.0);
            color.y = max(cos(iTime), 0.0);
            color.z = 0.5;
            finded = true;
        }
    }

    if (!finded)
    {
        color.x = sin(gl_FragCoord.x/iResolution.x * iTime);
        color.y = sin(gl_FragCoord.y/iResolution.y + iTime);
        color.z = cos(gl_FragCoord.y/iResolution.x + iTime);
    }
	fragColor = vec4(color, 1.0);
}
