#define WINDOW_SIZE 2.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 neighbourColor;
    vec4 neighbourTotal = vec4(0,0,0,0);

    float dx = 1.0 / iResolution.x;
    float dy = 1.0 / iResolution.y;

    float min = 1.0;
    float max = 0.0;
    float val = -100.0;

    for (float x = -WINDOW_SIZE; x < WINDOW_SIZE; x++) {
        for (float y = -WINDOW_SIZE; y < WINDOW_SIZE; y++) {
            neighbourColor = texture(iChannel0, vec2(uv.x + (x * dx), uv.y - (y * dy)));
            neighbourTotal += neighbourColor;
            val = (neighbourColor.r + neighbourColor.g + neighbourColor.b) / 3.;
            if (val > max) {
                max = val;
            } else if (val < min) {
                min = val;
            }
        }
    }

    float range = max - min;

    // Cartoonish 
    /*range *= 4.;
    if (range > 0.) {
   		range = 1. - range;
   		fragColor = neighbourTotal / 12. * range;
    } else {
        fragColor = neighbourTotal / 12.;
    }*/

    /*
    // Neons
    range *= 10.;
    fragColor = texture(iChannel0, uv) * range;
    */

    // Smoothish Black on White edges
    fragColor = vec4(pow(1. - range, WINDOW_SIZE * 3.));
    if ((fragColor.r + fragColor.g + fragColor.b) / 3. > 0.75) {
        fragColor = vec4(1,1,1,0);
    }
}
