void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 1.0 - 2.0*(fragCoord.xy / iResolution.xy);
   	float r = 5.0;
	vec2 rr = vec2(r,r);
    vec2 rxy = rr * uv;
    vec2 ex = fract(rxy);
    vec2 mx = 1.0 - 2.0*iMouse.xy / iResolution.xy;

    vec2 fx = vec2(mx.x - uv.x, (-1.0 * (mx.y - uv.y)));

    float l = length(fx);
    float p = 6.283 - atan(fx.y, fx.x);
    mat2 rm = mat2(cos(p), sin(p), -sin(p), cos(p));

    vec2 lxy = vec2(0.1,0.45);
    vec2 hxy = vec2(0.9,0.55);
    vec2 efx = (rm * (1.0 - 2.0*ex));
    vec2 eex = smoothstep(lxy, lxy*1.1, efx) 
        	 - smoothstep(hxy, hxy*1.1, efx);
    float bx = eex.x * eex.y;
	fragColor = vec4(bx,bx,bx,1.0);
}
