const mat3 YUV_to_RGB = mat3(
         1.0,1.0,1.0,
        0.0,-0.39465,2.03211,
        1.13983,-0.58060,0.0);

const mat3 XYZ_to_RGB = mat3(
    3.2406, -0.9689, 0.0557,
    -1.5372, 1.8758, -0.2040,
    -0.4986, 0.0415, 1.0570);

const vec3 D65_XYZ = vec3(.95047, 1.0, 1.08883);
const vec3 D65_xyY = vec3(0.31271, 0.32902, 1.0);
const vec3 D65_Yuv = vec3(1.0, 0.19784, 0.46834);
const vec3 ONE = vec3(1.0,1.0,1.0);

vec3 XYZ_to_xyY(vec3 XYZ) {
 	float inv_sum = 1.0/dot(XYZ,ONE);
    return XYZ.xyy * vec3(inv_sum,inv_sum,1.0);
}

vec3 xyY_to_XYZ(vec3 xyY) {
 	float s = xyY.z/xyY.y;
    float z = 1.0-dot(vec2(1.0,1.0), xyY.xy);
    return vec3(xyY.x, xyY.z, z) * vec3(s,1.0,s);
}

vec3 LUV_to_XYZ(vec3 LUV) {
    vec2 uv = LUV.yz / (13.0*LUV.x) + D65_Yuv.yz;
    float Y = LUV.x > 8.0 ? 
        D65_Yuv.x * pow((LUV.x+16.0)/116.0, 3.0) : 
    	LUV.x * D65_Yuv.x * 0.00110705645;
    float X = Y * 9.0 * uv.x / (4.0*uv.y);
    float Z = Y * (12.0-3.0*uv.x-20.0*uv.y)/(4.0*uv.y);
    return vec3(X,Y,Z);
}

vec3 YUV_spiral(float v, float r, float l, float L, float h, float H) {
    float hue = mix(h,H,v);
    float lum = mix(l,L,v);
    float amp = r*lum*(1.0-lum);
 	float a = 3.14159*2.0*(hue);
    vec3 c = vec3(lum,sin(a),cos(a));
   	c.yz *= amp;
    return YUV_to_RGB*c;
}

vec3 LUV_spiral(float v, float r, float l, float L, float h, float H) {
    float hue = mix(h,H,v);
    float lum = mix(l,L,v);
    float amp = r*lum*(1.0-lum);
 	float a = 3.14159*2.0*(hue);
    vec3 c = vec3(lum,sin(a),cos(a))*100.0;
   	c.yz *= amp;
    return XYZ_to_RGB*LUV_to_XYZ(c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    float g = floor(uv.y * 6.0);

    vec3 c = vec3(uv.x);

    if(g == 0.0) {
    	c = YUV_spiral(uv.x, 1.0, 0.0, 1.0, 0.3, 0.85);   
    }
    else if(g == 1.0) {
    	c = YUV_spiral(uv.x, 1.0, 0.0, 1.0, 0.0, 1.0);  
    }
    else if(g == 2.0) {
    	c = YUV_spiral(uv.x, 0.0, 0.0, 1.0, 0.0, 1.0);  
    }
    else if(g == 3.0) {
     	c = LUV_spiral(uv.x, 3.0, 0.0, 1.0, 0.3, 1.075);  
    }
    else if(g == 4.0) {
     	c = LUV_spiral(uv.x, 3.0, 0.0, 1.0, 0.0, 1.0);  
    }
    else if(g == 5.0) {
    	c = LUV_spiral(uv.x, 0.0, 0.0, 1.0, 0.0, 1.0);  
    }
    fragColor = vec4(c,1.0);
}
