#define FAR 100.0
#define STEPS 1028
#define EPS 0.01
#define PI acos( -1.0 )

float trig( float dist, float decay, float frequency, float amplitude, float speed )
{

	return exp(-decay * dist) *  
    sin(dist * frequency + ((iTime) * speed)) * amplitude;

}

float hash(vec2 p)  // replace this by something better
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float hashO( float x )
{

    return fract( sin( x ) * 4567.97 );

}

float noiseO( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) )*5.0, 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

float noise( in vec3 x )
{

    vec3 p = floor( x );
    vec3 k = fract( x );

    k *= k * k * ( 3.0 - 2.0 * k );

    float n = p.x + p.y * 57.0 + p.z * 113.0; 

    float a = hashO( n );
    float b = hashO( n + 1.0 );
    float c = hashO( n + 57.0 );
    float d = hashO( n + 58.0 );

    float e = hashO( n + 113.0 );
    float f = hashO( n + 114.0 );
    float g = hashO( n + 170.0 );
    float h = hashO( n + 171.0 );

    float res = mix( mix( mix ( a, b, k.x ), mix( c, d, k.x ), k.y ),
                     mix( mix ( e, f, k.x ), mix( g, h, k.x ), k.y ),
                     k.z
    				 );

    return res;

}

float fbm( in vec3 p )
{

    float wave = texture( iChannel0, vec2( 0.0, 0.25 ) ).x;
    float f = 0.0;
    f += 0.5000 * noise( p ); p *= 2.02; p -= wave;
    f += 0.2500 * noise( p ); p *= 2.03;
    f += 0.1250 * noise( p ); p *= 2.01;
    f += 0.0625 * noise( p );
    f += 0.0125 * noise( p );
    return f / 0.9375;

}

float map( vec3 p )
{

    vec4 mou = iMouse / iResolution.xyxy;
    float dist = distance( vec3( 2.0 ), p );
    float tri = trig( dist, 0.001, 20.0, 1.9, 0.2 );
    //float pla = p.y;
    float wave = texture( iChannel0, vec2( 0.0, 0.25 ) ).x;
    //return p.y + ( 2.0 * noiseO( p.xz + wave + ( iTime * 2.0 ) ) * fbm( p + wave + ( iTime * 2.0 )) );
	return p.y + noise( p * noise( p * wave ) ) * fbm( p * fbm( p * fbm( p + iTime * 0.2 ) ) );

}

vec3 grad( vec3 p )
{

    vec2 e = vec2( EPS, 0.0 );
    vec3 n = vec3( map( p + e.xyy ) - map( p - e.xyy ),
                   map( p + e.yxy ) - map( p - e.yxy ),
                   map( p + e.yyx ) - map( p - e.yyx )
                   );
    return n;

}

float softShadows( in vec3 ro, in vec3 rd )
{

    float res = 1.0;
    for( float t = 0.1; t < 8.0; ++t )
    {

        float h = map( ro + rd * t );
        if( h < EPS ) return 0.0;
        res = min( res, 8.0 * h / t );
        t += h;

    }

    return res;

}

vec3 shade( vec3 ro, vec3 rd, float t )
{

    vec3 pos = ro + rd * t;
    vec3 n = normalize( grad( pos ) );

    vec3 lig = normalize( vec3( 1.0, 0.8, 0.6 ) );
    vec3 blig = vec3( -lig.x, -lig.y, -lig.z );
    vec3 ref = reflect( rd, n );

    float dif = max( 0.0, dot( n, lig ) );
    float bac = max( 0.0, 0.2 + 0.8 * dot( n, blig ) );
    float spe = pow( clamp( dot( lig, ref ), 0.0, 1.0 ), 16.0 ); 
    float con = 1.0;
    float sha = softShadows( pos, lig );
    float amb = 0.5 + 0.5 * n.y;

    vec3 col = con * vec3( 0.1, 0.15, 0.2 );
    col += amb * vec3( 0.1, 0.15, 0.2 );
    col += dif * vec3( 1.0, 0.97, 0.85 ) * sha;
    col += bac * vec3( 1.0, 0.97, 0.85 );

    col = sqrt( col );
    col *= 0.6;
    col += 1.0 * spe;

    float wave = texture( iChannel0, vec2( 0.0, 0.75 ) ).x;
    vec3 tex = texture( iChannel1, reflect( rd, n ) ).rgb;

    col *= mix( vec3( 1.0, 0.0, 0 ), vec3( 0.0, 0.0, 1.0 ), fbm( pos + wave ) );
    col += 0.3 * tex;

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( -iResolution.xy + 2.0 * fragCoord ) / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mou = iMouse.xy / iResolution.xy;

    vec3 ro = 4.0 * vec3( sin( mou.x * 2.0 * PI ), 0.5, cos( -mou.x * 2.0 * PI ) );
    //vec3 rd = normalize( vec3( uv, -1.0 ) );
    vec3 ww = normalize( vec3( 0.0 ) - ro );
    vec3 uu = normalize( cross( vec3( 0.0, 1.0, 0.0 ), ww ) );
    vec3 vv = normalize( cross( ww, uu ) );
    vec3 rd = vec3( uv.x * uu + uv.y * vv + 1.5 * ww );

    float t = 0.0; float d = EPS;
    for( int i = 0; i < STEPS; ++i )
    {

        d = 0.5 * map( ro + rd * t );
        if( d < EPS || t > FAR ) break;

        t += d;

    }

    // Time varying pixel color
    vec3 col = d < EPS ? shade( ro, rd, t ) : mix( vec3( 0.3, 0.2, 0.4 ), vec3( 0.0, 0.1, 0.2 ), uv.y );

    // Output to screen
    fragColor = vec4(col,1.0);
}
