void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //vec4 textureGrad( sampler? , vec? coord, vec2 dPdx, vec2 dPdy)
    vec2 dPdx = vec2(iMouse.x*0.0002, iMouse.y*0.0002);
    vec2 dPdy = vec2(0., 0.);

    vec4 col = textureGrad(iChannel0, uv, dPdx, dPdy);


    vec4 t1 = textureGrad(iChannel0, uv, vec2(0.0, 0.02), vec2(0));
    vec4 t2 = textureGrad(iChannel0, uv, vec2(0.0, 0.0), vec2(0.02, 0.0));

    // Dont mind the blinking, I either test t1-t2, or col
    fragColor = mix( vec4(t1-t2), col, fract(iTime*.5) );
}
