//Sucky try at Shepard tone illusion (https://en.wikipedia.org/wiki/Shepard_tone)
const float root12=pow(2.0,1.0/12.0);
const float freqA=440.0;

//C-Major scale
const int[] halves=int[](3,5,7,8,10,12,14);

vec2 mainSound( in int samp, float time )
{
    int stp=int(mod(time*1.5,7.0));

    float amp=float(stp)*1.0;
    float namp=(7.0-float(stp))*1.0;
    float base_amp=0.0;
    float smp1=(namp+base_amp)*sin(freqA*pow(root12,float(halves[stp]))*time);
    float smp2=(amp+base_amp)*sin(freqA*pow(root12,float(halves[stp])-12.0)*time);
    return vec2(smp1+smp2,smp1+smp2);    
}
