#define RADIUS .02
#define NUM 50.
#define THCKN .002
#define BLUE vec4(.11, .15, .2, 1.)
#define WHITE vec4(1., .98, .9, 1.)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
    vec2 pos = (fragCoord -.5 * lM) / lM.x;

    if (abs(pos.x) < .1 + RADIUS + THCKN && abs(pos.y) < .05 + RADIUS + THCKN) {
        for (float i = 0.; i < NUM; i++){
            float timed = iTime * 1.1 + i * .05;
            vec2 circPos = vec2(sin(timed / .3) * .1, sin(timed / .25) *.05);
            float dist = abs(distance(pos, circPos) - RADIUS * (i / NUM) + THCKN);
            if(dist < THCKN) {
                fragColor = mix(BLUE, WHITE, smoothstep(THCKN, THCKN * .5, dist));
                return;
            }
        }
    }

    fragColor = BLUE;
}
