// ax+by+c=0

float a=1.0;
float b=1.0;
float c=1.0;


float res=20.0;

vec4 main_color=vec4(1.0,1.0,1.0,1.0);

void equals_zero(in float condition,in float thickness,in vec4 color)
{
	if ((condition>-(thickness*0.001*res))&&(condition<(thickness*0.001*res)))
	{
		main_color=color+main_color*(1.0-color.a);
	}
}

void grid(in vec2 point)
{
	equals_zero(point.x-floor(point.x+0.5),1.0,vec4(0.0,0.0,0.0,0.5));
	equals_zero(point.y-floor(point.y+0.5),1.0,vec4(0.0,0.0,0.0,0.5));
	equals_zero(point.x,1.0,vec4(1.0,0.0,0.0,1.0));
	equals_zero(point.y,1.0,vec4(1.0,0.0,0.0,1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 point = (((fragCoord.xy / iResolution.x)-vec2(0.5,0.5*(iResolution.y/iResolution.x))))*res;

	grid(point);

	//a=1.0*sin(iTime);
	//b=1.0*cos(iTime);	


	equals_zero((a*point.x+b*point.y+c)/(sqrt(a*a+b*b)),2.0,vec4(0.0,0.0,0.0,1.0));

	fragColor = main_color;
}
