void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
	float mindim = 20.0;
	float maxdim = 100.0;
	float speed = 1.0;

	float tsin = (sin(3.14*(mod(iTime*speed,10.0)/10.0*2.0 -1.0)) + 1.0)/2.0;
	vec2  dsin = sin(3.14*(uv/iResolution.xy*2.0 - vec2(1)));

	float dim = mindim + (maxdim - mindim)*tsin;
	vec2 f = mod(floor(uv/(dim + 0.3*dim*dsin)),2.0);
	float t = abs(f.x - f.y);
	fragColor = vec4(vec3(t),1);
}
