void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = -1.70 * iTime;
    vec2 uv = 3.0 * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec4 vari = vec4(0.6 , 0.1 , 0.7 , 1.0);  //taustan v√§ri
    float wave=  2.0 - abs(length(uv.y - sin(fragCoord.x/40.0+time) )*7.0 ) ;
    float wave2=  2.0 - abs(length(uv.y - cos(fragCoord.x/40.0+time*1.4) )*7.0 ) ;

    vec4 color=(sin(fragCoord.x/7.0)*vari + sin(fragCoord.y/7.0)*vari);
    fragColor = color/(wave*wave2);


}
