const float PI = 3.141592;
const int imax=0;

float sine(float v){
	return (sin(v*PI/2.)+1.)/2.;
}

vec3 getPix(float v){
	vec2 res = iChannelResolution[0].xy;
	vec2 inc = 1./res;

	v = floor(mod(v,res.x*res.y));

	vec2 pos = vec2(floor(mod(v,res.x)),
		     	    floor(v/res.x));

	return texture(iChannel0,pos*inc).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*(fragCoord.xy / iResolution.y)-vec2(iResolution.x/iResolution.y,1.);

	float div=10.;

	float ang = (atan(uv.x,uv.y)/PI)/2.+0.5;

	float d = length(uv*div)+ang+iTime;

	float t=5.;

	float modif = mix(sine(ang*32.),sine(ang*64.),sine(length(uv)*PI*10.));

	modif*=sine(iTime);

	vec3 col1 = getPix(d+modif);
	vec3 col2 = getPix(d+modif+1.);

	vec3 subc = mix(col1,col2,1.-ang);



	for(int i=0 ; i<imax ; i++){
		subc*=subc+((sin(iTime)+1.)/0.5)/float(imax)+0.5;
	}

	vec3 color = vec3(subc);
	fragColor = vec4(color,1.0);
}
