const vec4 bgc = vec4(0.0); //%background color
const vec4 lc = vec4(0.40, 0.32, 0.832, 1.0); //%line color

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float lw = 2.0 / iResolution.y; //%line width

    vec2 uv = (fragCoord - .5 * iResolution.xy ) / iResolution.y ;
	//vec2 uv = fragCoord.xy / iResolution.xy;
    //uv = uv - 0.5; //%move origin to (0.5, 0.5)

    float rad = iTime;
    uv.y = cos(rad)*uv.x + sin(rad) * uv.y;//rotate rad per frame

    float alpha = smoothstep(0.0, lw, abs(uv.y)); //%AA

    fragColor = mix(lc, bgc, alpha);
}
