// Simplex 2D noise
// from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}


vec2 snoise(vec2 v, float t, float diff) {
	return vec2(snoise(vec2(v.x, t)), snoise(vec2(v.y, t + diff)));
}

#define PI 3.14159265
mat2 rotate(float a) {
	return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float tri(float i) {
    float m = 1.0;
	return m - abs(mod(i, 2.0*m) - m);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord.xy -0.5 * iResolution.xy) / iResolution.x;
    uv += snoise(uv, iTime / 50.0, 300.0);
    float zoom = 20.0;
    float r = tri(uv.x * zoom);
    float g = tri(uv.y * zoom);
    uv = uv * rotate(PI / 4.0);
    float b = tri(uv.x * zoom);
	fragColor = vec4(r, 0.0, b, 1.0);
}
