void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    //time for fade in/out
    float u = 2.;
    float o = 0.5;

    float t = (2.*u+1.)*fract(iDate.w/(2.*u+1.));

    float r = (-abs(t - u)+u);
    float g = (-abs(t - u+o)+u);
    float b = (-abs(t - u+2.*o)+u);

    r = clamp(r, 0., 1.);
    g = clamp(g, 0., 1.);
    b = clamp(b, 0., 1.);

	fragColor = vec4(texture(iChannel0, uv))*vec4(r,g,b,1.0);
}
