void mainImage( out vec4 O, vec2 U )
{
    U /= O.xy = iResolution.xy;

    vec3 l = vec3(0, 2., 0),
         p = vec3(U*2.-1.,0).xzy * (1.-length(iMouse.xy/O.xy-.5)) *5. - l,
         P = normalize(p-l),
         n = normalize( l + P / cos(1.57*P.y) );

	O = texture(iChannel0,n);
}
