// Gigatron France quick code ;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv =  -1.+2.*fragCoord.xy / iResolution.xy ;

    vec2 p = uv  ;
    float t = mod(iTime*0.4,128.0);
    float tt = sin(t)*3.;

    if(p.x>0.0 && p.x<2.0)   {    p.x +=   p.x /16.+asin(0.1+uv.y/8.0*tt*t)*p.x+t   ;

                   }

    if(p.x>-2. && p.x<0.0)   {    p.x +=  p.x /16.0+asin(0.1+uv.y/8.0*tt*t)*p.x +t   ;

                   }

    p.x = p.x - t*0.9;



    p = fract(vec2(p.x*8. ,p.y*4.));



    vec3 tx 	= texture( iChannel0, p ).rgb;

         tx.r = tx.r*tx.g-tx.b*pow(p.y,2.)-length(p)/5.; 
         tx 	+= texture( iChannel2, p/1.2+(tt/2.) ).rgb*tt;
         tx     /= texture( iChannel0, uv/1.02+(tt*0.4) ).rgb*tt;



	fragColor = vec4(tx*0.1,1.0)*sqrt(tx.r+tt);
}
