// VCR style inspired from https://www.shadertoy.com/view/ldjGzV

#define t iTime
#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)
float C,S;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float hs=step(.3,sin(t+2.+4.*sin(t*9.)))*(sin(uv.y*10.+t*5.)+sin(t)*sin(t*20.)*.5)*.05;
    uv.x+=hs;
    float vs=step(.8,sin(t+2.*sin(t*4.)))*(sin(t)*sin(t*20.)+cos(t)*sin(t*200.)*.1);
    uv.y=fract(uv.y+vs);
    vec3 c=texture(iChannel0,uv).rgb;
    c.xy*=rot(iTime*.3);
    c.yz*=rot(iTime*.5);
    c.xy*=rot(iTime*.7);
    c.yz*=rot(iTime*.9);
	fragColor = vec4(floor(5.*abs(c))*.2,1.0);
}
