void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float zoom = 10. + floor(iTime*30.) / 100000.;
    //zoom = floor(iTime);
	vec2 uv = (zoom) * ((fragCoord.xy - (iResolution.xy / 2.0)));

    //float r = mod(iTime / 100.0 + acos(-1.0) / 4.0, acos(-1.0)*2.0);
    //r = atan(uv.y, uv.x);
    //mat2 rot = mat2(cos(r), -sin(r), sin(r), cos(r));
	//uv = uv * rot;

    highp float x = uv.x;
    highp float y = uv.y;

    highp float value = (x*x) + (y*y);
    //float f = 10. + (iTime/2.)/10.;
    float exponent =  floor(log2(value));
    float mantissa =  value*pow(2.0, -exponent)-1.;

    //lowp float d2 = (x*x + y*y) / f;

    float r = mantissa - floor(mantissa*exp2(16.)+0.5)/exp2(16.);
    float color = sign((r));

    //float low = abs(color);
    //float med = abs(color) - 1.0;
    //float high = abs(color) - 2.0;
    if(color > 0.0) {
   	  //fragColor = vec4(low, med, high,1.0);
      fragColor = color - vec4(0,1,1,0); 
    } else {
      //fragColor = vec4(med, med, low,1.0);
      fragColor = -color - vec4(1,1,0,0); 
   }
}
