void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 sin_color = vec4(uv,0.5+0.5*sin(iTime),1.0);
	float at = iResolution.y/2.0;/*Place the string on the middle of y axis*/
	float l = 150.0; /*+/- Ymax*/
	float thickness = .068;
	float speed = 20.0;
	float amplitude = l - 100.0;
	float magnification = 100.0;
	vec4 line_color = sin_color;
	vec4 background_color = vec4(0.0,0.0,0.0,0.0);/*Black color*/

	if(  fragCoord.y >  at + l || fragCoord.y < at - l ){
			return;
	}


	/* sin(X) where X = 1.71*fragCoord.x + iTime*speed
	speed = 0.9;
	uv.y = (sin(1.71*fragCoord.x+(iTime*speed)))*800.0;*/

	uv.y = (sin(uv.x*amplitude+(iTime*speed)))*magnification+at;

	if(uv.y - at*thickness  < fragCoord.y && fragCoord.y <= at*thickness+ uv.y )
		fragColor = line_color;
//	else
		//fragColor = background_color;
	return;




}
