#define PI     3.14159265358

// updated saw() from @ollj
float sawNew(float rads) {
    rads = rads / (PI * 2.) + PI / 4.;
    rads = fract(rads); 
    return -1. + 2. * abs(rads * 2. - 1.);
}

float saw(float rads) {
    rads += PI * 0.5;
    float percent = fract(rads/PI);				
    float dir = sign(sin(rads));
    return dir * (2. * percent  - 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;									
	vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float amp = 0.06;
    float freq = 25.;
    float numLines = 70. + 25. * sin(time);
    float sawWaveDisp = saw(uv.x * freq);
    float sinWaveDisp = sin(uv.x * freq);
    uv.y += amp * mix(sawWaveDisp, sinWaveDisp, 0.5 + 0.5 * sin(time));
    float col = 0.5 + 0.5 * sin(uv.y * numLines);
    float aA = 1./(iResolution.y * 0.01) ;
    col = smoothstep(0.5 - aA, 0.5 + aA, col);
	fragColor = vec4(vec3(col),1.0);
}
