#define VIEWPORT 7.
#define POSITION vec2(0.0,0.)

#define pi 3.14159265


vec2 cpow(vec2 b, vec2 e){
	float ab = atan(b.y,b.x);
	float lgb = log(b.x*b.x+b.y*b.y)/2.0;
	float lr = exp(lgb*e.x-ab*e.y);
	float cis = lgb*e.y+ab*e.x;
	return vec2(cos(cis)*lr, sin(cis)*lr);
}
vec2 cexp(vec2 x){
	return vec2(cos(x.y),sin(x.y))*exp(x.x);
}
vec2 clog(vec2 x){
	return vec2(log(x.x*x.x+x.y*x.y)/2.0,atan(x.y,x.x));
}
vec2 cmul(vec2 a, vec2 b){
	return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
}
vec2 cdiv(vec2 a, vec2 b){
	return vec2((a.x*b.x+a.y*b.y), (a.y*b.x-a.x*b.y))/(b.x*b.x+b.y*b.y);
}
vec2 csin(vec2 x){
    return vec2(sin(x.x)*cosh(x.y),sinh(x.y)*cos(x.x));
}
vec2 ccos(vec2 x){
    return vec2(cos(x.x)*cosh(x.y),sinh(x.y)*sin(x.x));
}

vec3 hueValue(vec2 hv){
    vec3 color = clamp( abs(mod(hv.x*6.0+vec3(0.,4.,2.),6.)-3.)-1., 0., 1. );
    if(hv.y<0.5)return mix(vec3(0.),color,hv.y*2.);
    return mix(color,vec3(1.),(hv.y-.5)*2.);
}


vec2 f(vec2 z){
    return cpow(z,vec2(-1.0,0.0));//+vec2(cos(iTime),sin(iTime));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 z = fragCoord.xy / iResolution.xy;
    vec2 w;
    vec3 col;
    z=z-vec2(.5);
    z.x=z.x*iResolution.x/iResolution.y;
    z=z*VIEWPORT+POSITION;

    w=f(z);

	col = vec3(hueValue(vec2(0.5*atan(w.y,w.x)/pi,1./(length(w)+1.))));

    //use polar grid
    //w = vec2(length(w),atan(w.y,w.x)*4.0/pi);
    //grid
    col = col * (1.0 - min(pow(fract(w.x),40.0) + pow(fract(w.y),40.0), 1.0));

    fragColor = vec4(col,1.0);

}
