{
	vec2 uv = fragCoord.xy /iResolution.xy;
    uv -=.5;
    uv.y	*=	iResolution.y/iResolution.x;
    float Range = 1.;
	float Line_Smooth	= pow(smoothstep(Range,Range-.05,2.*Range*(abs(smoothstep(.0, Range,uv.x+.5 )-.5))),.2);                         


    float Factor_T    =floor(iTime*15.);
    float Factor_X    =xRandom(uv.x*.001);
    float Amplitude1  =0.5000 * noise(Factor_X, Factor_T)
         			  +0.2500 * noise(Factor_X, Factor_T)
          			  +0.1250 * noise(Factor_X, Factor_T)
    	  			  +0.0625 * noise(Factor_X, Factor_T);
	Factor_X    	  =xRandom(uv.x*.001+.0005);
    float Amplitude2  =0.5000 * noise2(Factor_X, Factor_T)
         			  +0.2500 * noise2(Factor_X, Factor_T)
          			  +0.1250 * noise2(Factor_X, Factor_T)
    	  			  +0.0625 * noise2(Factor_X, Factor_T);

    vec4 Light_Color=vec4(.3, .45, 1., 1.);	
    vec4 Light_Color2=vec4(.752, .164, .842, 1.);


    float Light_Track  = line(vec2(uv.x,uv.y+(Amplitude1-.5)*.12*Line_Smooth), .005, .0);
   	float Light_Track2  = line(vec2(uv.x,uv.y+(Amplitude2-.5)*.16*Line_Smooth), .005, .0);

	vec4 line1 =  vec4(Light_Track)*Light_Color;
    vec4 line2 =  vec4(Light_Track2)*Light_Color2;



    fragColor =(line1+line2);
}
