// quick doodle inspired by https://twitter.com/aliceffekt/status/529387010759602177
// I should have stayed truer to the original
// but I'm tired and I guess it didn't go quite to plan :(
// oh well...

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float t=iTime+80.;
	vec2 uv = fragCoord.xy / iResolution.yy;
    uv.y*=2.;
    uv*=mat2(.4,.3,-.3,.4)*0.3;
    float z=exp(-uv.y*uv.y*(2000.+1000.*cos(t*0.074+0.36)))*((2.+sin(t*0.035))*0.2)+0.8*cos(uv.y*11.0);
   uv.x+=0.7*sin(z+t*0.01)*0.3;
    float d=sin(uv.x*1200.)+0.3;
    float aa=fwidth(d);
    float c=smoothstep(-aa,aa,d);
	fragColor = vec4(c,c,c,1.0);
}
