vec2 cinv( vec2 z)  { float d = dot(z,z); return vec2( z.x, -z.y ) / d; }
vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }

vec2 p;
float bailout = 1e12;
float k = .03;


vec2 f1( vec2 x ){return csqr(x) + p  ;}//Mandelbrot
vec2 f2( vec2 x ){return cinv(x+p)+ x-p  ;}

vec2 f( vec2 x ){float t = 0.5+0.5*cos(0.1*iTime);return (f1(x)*t+f2(x)*(1.0-t));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	p = 2.*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

	vec2 z = p;
    vec2 zn = csqr(z)/dot(z,z);
	for( int i=0; i<60; i++ ) 
	{				   

        z=f(z);
        if(dot(z,z)>bailout){
            //float k1 = pow(bailout/dot(z,z),.125)*k;
            float k1 =min(max((1.-log2( (log(dot(z,z))/log(bailout)) )),0.),1.)*k;
            zn = (1.-k1)*zn+k1*csqr(z)/dot(z,z);
            break;}
        zn = (1.-k)*zn+k*csqr(z)/dot(z,z);
	}

	//float f = (1.-exp(-.5*dot(z,z)));
    float f = (1.-exp(-dot(zn,zn)));
	vec3 color=3.*f*abs(vec3(zn.x,zn.x*zn.y,zn.y*zn.y));
	fragColor = vec4(color,1.0);
}
