//
// aspect ratio correct UV.
// y range [ -1.1 .. 1.1 ]
// x range [ -AR*1.1 .. AR*1.1 ]
//
vec2 toUV( vec2 p )
{
    vec2 uv = -1.1 + 2.2 * p / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    return uv;
}

void mainImage( out vec4 color, in vec2 p )
{
    vec2 uv = toUV(p);
    vec2 muv = toUV(iMouse.xy);

    float f = dot(uv, muv);

    // display positive as red, negative as blue
    vec3 c = vec3(clamp(f,0.,1.),0.,clamp(-f,0.,1.));

    // position of 0:0
    c = mix(c, vec3(1.,0.,0.), smoothstep(0.006, 0.0, abs(uv.x))+smoothstep(0.006, 0.0, abs(uv.y)));
    // position of 1:1 and -1:-1
    c = mix(c, vec3(1.,1.,0.), smoothstep(0.006, 0.0, abs(uv.x-1.))+smoothstep(0.006, 0.0, abs(uv.y-1.)));
    c = mix(c, vec3(0.,1.,1.), smoothstep(0.006, 0.0, abs(uv.x+1.))+smoothstep(0.006, 0.0, abs(uv.y+1.)));

    // position of mouse click
    c = mix(c, vec3(0.,1.,0.), smoothstep(0.006, 0.0, abs(uv.x-muv.x))+smoothstep(0.006, 0.0, abs(uv.y-muv.y)));
	color = vec4(c, 1.0);
}
