void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float color = 0.5;

    //
    vec2 leftSamplePosition = vec2(fragCoord.x - 1.0, fragCoord.y) / iResolution.xy;
    vec3 leftSample = texture(iChannel0, leftSamplePosition).rgb;

    vec2 leftTopSamplePosition = vec2(fragCoord.x - 1.0, fragCoord.y + 1.0) / iResolution.xy;
    vec3 leftTopSample = texture(iChannel0, leftTopSamplePosition).rgb;

    vec2 leftBottomSamplePosition = vec2(fragCoord.x - 1.0, fragCoord.y - 1.0) / iResolution.xy;
    vec3 leftBottomSample = texture(iChannel0, leftBottomSamplePosition).rgb;
    ///


    //
    vec2 middleTopSamplePosition = vec2(fragCoord.x, fragCoord.y -1.0) / iResolution.xy;
    vec3 middleTopSample = texture(iChannel0, middleTopSamplePosition).rgb;

    vec2 middleSamplePosition = vec2(fragCoord.x, fragCoord.y) / iResolution.xy;
    vec3 middleSample = texture(iChannel0, middleTopSamplePosition).rgb;

    vec2 middleBottomSamplePosition = vec2(fragCoord.x, fragCoord.y + 1.0) / iResolution.xy;
    vec3 middleBottomSample = texture(iChannel0, middleBottomSamplePosition).rgb;
    ///


    //
    vec2 rightSamplePosition = vec2(fragCoord.x + 1.0, fragCoord.y) / iResolution.xy;
    vec3 rightSample = texture(iChannel0, rightSamplePosition).rgb;

    vec2 rightTopSamplePosition = vec2(fragCoord.x + 1.0, fragCoord.y + 1.0) / iResolution.xy;
    vec3 rightTopSample = texture(iChannel0, rightTopSamplePosition).rgb;

    vec2 rightBottomSamplePosition = vec2(fragCoord.x + 1.0, fragCoord.y - 1.0) / iResolution.xy;
    vec3 rightBottomSample = texture(iChannel0, rightBottomSamplePosition).rgb;
    ///

    //
    float sumOfWhite = 0.0;

    if(leftSample.r < 0.5) sumOfWhite += 1.0;
    if(leftTopSample.r < 0.5) sumOfWhite += 1.0;
    if(leftBottomSample.r < 0.5) sumOfWhite += 1.0;

    if(rightSample.r < 0.5) sumOfWhite += 1.0;
    if(rightTopSample.r < 0.5) sumOfWhite += 1.0;
    if(rightBottomSample.r < 0.5) sumOfWhite += 1.0;

    if(middleTopSample.r < 0.5) sumOfWhite += 1.0;
    if(middleBottomSample.r < 0.5) sumOfWhite += 1.0;

    color += (sumOfWhite / 8.0);
    ///

    //
    float sumOfBlack = 0.0;
    float someValue = /*0.5*/ middleSample.r;

    if(leftSample.r > someValue) sumOfBlack += 1.0;
    if(leftTopSample.r > someValue) sumOfBlack += 1.0;
    if(leftBottomSample.r > someValue) sumOfBlack += 1.0;

    if(rightSample.r > someValue) sumOfBlack += 1.0;
    if(rightTopSample.r > someValue) sumOfBlack += 1.0;
    if(rightBottomSample.r > someValue) sumOfBlack += 1.0;

    if(middleTopSample.r > someValue) sumOfBlack += 1.0;
    if(middleBottomSample.r > someValue) sumOfBlack += 1.0;

    color -= (sumOfBlack / 8.0);
    ///

	fragColor = vec4(vec3(color),1.0);
}
