const float lineNum = 16.0;
const float variation = 0.5;
const float speed = 0.01;
const vec3 baseColor = vec3(1.0, 0.5, 0.1);
const float glowing = 0.007;
const float glowSpeed = 1.5;
const float petalNum = 6.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 pos = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y) * 0.5;

	float radius = length(pos) * 3.0 - 1.0;
	float t = atan(pos.y + variation, pos.x);

	float color = 0.0;
	for (float i = 1.0; i <= lineNum; i++) {
		color += glowing * (cos(iTime * glowSpeed) / 3.0 + 1.0) / abs(
            0.2 * sin(
                petalNum * (t + i * iTime * speed)
            ) - radius
        );
	}

	fragColor = vec4(baseColor * color, 1);
}

const float vibratoSpeed = 1.5; // it should equal to glowSpeed

vec2 mainSound( in int samp, float time )
{
    return vec2(
        sin(3.14 * 340.0 * time) // base sound
        * ((abs(cos(time * vibratoSpeed / 2.0)) + 0.3) * 0.3) // vibrato
    );
}
