#define PI 3.1415926535
float dist(vec2 p0, vec2 pf){return sqrt((pf.x-p0.x)*(pf.x-p0.x)+(pf.y-p0.y)*(pf.y-p0.y));}

vec3 difference (vec3 target, vec3 blend){
    return abs (target - blend);

}

vec3 radialGradient(vec2 location, float size, vec3 inner, vec3 outer, vec2 fragCoord)
{
    float d = dist(iResolution.xy*location,fragCoord.xy);
    return mix(inner, outer, d/size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float sinSeed = sin(iTime);
    float cosSeed = cos(iTime);
    float time = iTime;

    vec3 color = vec3(0.0);

    float mul = 1.;
    for(float j = 0.; j < mul; j += 1.) {
        float amt = 2.95;
        for(float i = 0.1; i < amt; i += 0.1) {
            vec2 loc1 = vec2(0.5 + sin(time+i+j)*0.2, 0.5 + cos(time+i+j)*0.2);
            vec3 c1 = radialGradient(loc1, 10., vec3(0.0), vec3(1.0), fragCoord);
            color = difference(color, c1);
            vec2 loc2 = vec2(0.5 + sin(time+i+j+PI)*0.2, 0.5 + cos(time+i+j+PI)*0.2);
            vec3 c2 = radialGradient(loc2, 10., vec3(0.0), vec3(0.2), fragCoord);
            color = difference(color, c2);
        }
    }


	fragColor = vec4(color, 1.0);
}
